let buttonwrite = document.querySelector(".button-write-us"),
    popup = document.querySelector(".popup"),
    buttonclose = document.querySelector(".button-close");
buttonwrite.addEventListener("click", function(e) { e.preventDefault(), popup.classList.remove("visually-hidden"), popup.classList.add("bounce") }), buttonclose.addEventListener("click", function() { popup.classList.add("visually-hidden"), popup.classList.remove("bounce") });