let buttonwrite = document.querySelector(".button-write-us"),
    popup = document.querySelector(".popup"),
    buttonclose = document.querySelector(".button-close"),
    names = document.querySelector("[name=name]"),
    email = document.querySelector("[name=email]"),
    form = document.querySelector(".appointment-form "),
    button = document.querySelector(".button-popup");
buttonwrite.addEventListener("click", function(e) { e.preventDefault(), popup.classList.remove("visually-hidden"), popup.classList.add("bounce") }), form.addEventListener("submit", function(e) { names.value && email.value || (e.preventDefault(), popup.classList.remove("bounce"), popup.classList.remove("error"), popup.classList.add("error")) }), buttonclose.addEventListener("click", function() { popup.classList.add("visually-hidden"), popup.classList.remove("bounce") });